import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';

import { PureIncludeRatingsOnlyFilter } from './IncludeRatingsOnlyFilter';

describe.only('<PureIncludeRatingsOnlyFilter />', () => {
  let props;

  beforeEach(() => {
    props = {
      current: false,
      set: jest.fn(),
    };
  });

  it('checked snapshot', () => {
    const wrapper = shallowWithIntl(<PureIncludeRatingsOnlyFilter {...props} />);

    expect(wrapper).toMatchSnapshot();
  });

  it('unchecked snapshot', () => {
    props.current = true;

    const wrapper = shallowWithIntl(<PureIncludeRatingsOnlyFilter {...props} />);

    expect(wrapper).toMatchSnapshot();
  });

  it('should call toggleRatingsOnly when Checkbox is clicked', () => {
    const wrapper = shallowWithIntl(<PureIncludeRatingsOnlyFilter {...props} />);

    wrapper.find('Checkbox').simulate('click');

    expect(props.set).toHaveBeenCalled();
  });
});
