import React from 'react';
import { FormattedMessage, InjectedIntlProps, injectIntl } from 'react-intl';

import { ModalWrapper } from 'gca-react-components';

interface ErrorModalProps {
  closeModal(): void;
}

interface Props extends ErrorModalProps, InjectedIntlProps {}

const ErrorModal = (props: Props) => (
  <ModalWrapper
    title={props.intl.formatMessage({ id: 'modals.error.title' })}
    confirmButtonText={props.intl.formatMessage({ id: 'modals.button.ok' })}
    onConfirm={props.closeModal}
    onCancel={props.closeModal}
  >
    <FormattedMessage tagName="p" id="modals.error.body" />
  </ModalWrapper>
);

export default injectIntl<ErrorModalProps>(ErrorModal);

export { ErrorModal as PureErrorModal };
