export type Locale =
  | 'de-de'
  | 'en-au'
  | 'en-gb'
  | 'en-us'
  | 'es-es'
  | 'es-mx'
  | 'fr-ca'
  | 'fr-fr'
  | 'it-it'
  | 'ja-jp'
  | 'nl-nl';

export const momentLocales = {
  'de-de': 'de',
  'en-au': 'en-au',
  'en-gb': 'en-gb',
  'en-us': 'en',
  'es-es': 'es',
  'es-mx': 'es',
  'fr-ca': 'fr-ca',
  'fr-fr': 'fr',
  'it-it': 'it',
  'ja-jp': 'ja',
  'nl-nl': 'nl',
};

export function getMomentLocale(locale: Locale) {
  return momentLocales[locale] !== undefined ? momentLocales[locale] : 'en';
}
