import keyBy from 'lodash/keyBy';

/**
 *  A helper function for turning arrays into maps, keyed by a unique identifier.
 *  Useful for creating a normalized redux state.
 *
 * @param {Object} array    Array of objects, each of which must have a unique identifier
 * @param {String} key      Key to use in building the overall object
 *
 * @returns {Object}
 *
 * example:
 *
 * const reviews = [{ reviewId: 'a1', body: 'some review'}, { reviewId: 'b2', body: 'another review' }];
 * const reviewsMap = transformArrayToObject(reviews, 'reviewId');
 * console.log(reviewsMap)
 * > { a1: { reviewId: 'a1', body: 'some review'}, b2: { reviewId: 'b2', body: 'another review' } }
 *
 */

export const transformArrayToObject = keyBy;

