import { shallow } from 'enzyme';
import React from 'react';

import MarketingPage from './MarketingPage';

import CompareProducts from './CompareProducts';
import MarketingFooter from './MarketingFooter';
import MarketingIntro from './MarketingIntro';
import MarketingValueDetails from './MarketingValueDetails';
import MarketingValueProps from './MarketingValueProps';
import MarketingVideo from './MarketingVideo';


describe('Marketing Page', () => {
  const setup = () => {
    const pageShallow = shallow(<MarketingPage />);

    return { pageShallow };
  };

  // todo: move this in a separate test file for the MarketingIntro section
  xit('should have a Get Started button', () => {
    const { pageShallow } = setup();

    expect(pageShallow.find('.intro-text [data-testid="ctaButton"]').length).toBe(1);
  });

  it('should have main sections', () => {
    const { pageShallow } = setup();

    expect(pageShallow.find(MarketingIntro).length).toBe(1);
    expect(pageShallow.find(MarketingValueProps).length).toBe(1);
    expect(pageShallow.find(MarketingValueDetails).length).toBe(1);
    expect(pageShallow.find(CompareProducts).length).toBe(1);
    expect(pageShallow.find(MarketingVideo).length).toBe(1);
    expect(pageShallow.find(MarketingFooter).length).toBe(1);
  });
});
