import { Icon } from '@buffet/icon';
import React from 'react';
import { FormattedHTMLMessage, FormattedMessage } from 'react-intl';
import './ValueDetails.scss';

interface ValueDetailsProps {
  titleId: string;
  explanationId: string;
  supportingFactsIds: string[];
  image: string;
}

type Props = ValueDetailsProps;

const ValueDetails = ({ image, titleId, explanationId, supportingFactsIds }: Props) => {
  return (
    <section className="section value-details">
      <div className="section-content section-row dual-panel">
        <div className="value-text">
          <h1 className="section-title">
            <FormattedMessage id={titleId} />
          </h1>
          <div className="explanation">
            <FormattedMessage id={explanationId} />
          </div>
          <ul className="value-facts">
            {supportingFactsIds.map(translationKey => {
              return (
                <li key={translationKey}>
                  <Icon iconId="checkmark" className="checkmark" />
                  <FormattedHTMLMessage id={translationKey} />
                </li>
              );
            })}
          </ul>
        </div>
        <div className="value-image">
          <img src={image} />
        </div>
      </div>
    </section>
  );
};

export default ValueDetails;
