const fs = require('fs');
const _ = require('lodash');
const path = require('path');
const logBlock = require('./helpers/log-block');

const defaultEnv = {
  APP: 'reviews',
  ENABLE_TUNNEL: 'false',
  MOCK: 'false',
  WITH_NG: 'true',
  NODE_ENV: 'development',
}

const defaultEnvString = _.map(defaultEnv, (value, key) => `${key}=${value}`).join('\n');

fs.writeFile(path.resolve('.env'), defaultEnvString, (err) => {
  if (err) {
    console.log('Something went wrong and we could not write to .env file');
    console.log(err);
  }

  logBlock('Wrote .env file:', defaultEnv)
});