import React from 'react';

interface AlertProps {
  canClose?: boolean;
  children: React.ReactFragment;
  display: 'inline' | 'block';
  onClose?: () => void;
  type: 'success' | 'error' | 'warn';
}

type Props = AlertProps & {
  active: boolean;
  delayTimeout?: number;
  fadeOut?: boolean;
  inactiveContent?: React.ReactFragment;
};

declare class AlertMessage extends React.Component<Props, {}> {}

export default AlertMessage;
