import { shallow } from 'enzyme';
import React from 'react';

import { BarChart } from './BarChart';

describe('<BarChart />', () => {
  it('snapshot with no data', () => {
    const wrapper = shallow(<BarChart />);

    expect(wrapper).toMatchSnapshot();
  });

  it('snapshot with all props', () => {
    const data = [
      { key: '5', value: 175 },
      { key: '4', value: 200 },
      { key: '3', value: 50 },
      { key: '2', value: 10 },
      { key: '1', value: 5 },
    ];

    const wrapper = shallow(<BarChart data={data} color="#000" />);

    expect(wrapper).toMatchSnapshot();
  });
});
