import React from 'react';
import * as CropperJS from 'cropperjs';

export type CropperJSInstance = CropperJS;

interface Props extends CropperJS.CropperOptions{
  cropperHeight: number,
  cropperWidth: number,
  minRelativeHeight?: number,
  minRelativeWidth?: number,
  originalHeight?: number,
  originalWidth?: number,
  src: string,
  cropperRef?(ref?: CropperJSInstance): void,
}

export class Cropper extends React.Component<Props> {}
export default Cropper;
