import React from 'react';
import { DateRangePicker as AirBnbDateRangePicker } from 'react-dates';
import 'react-dates/lib/css/_datepicker.css';
import './DateRangePicker.scss';

import { Chevron } from '../../icons/Chevron';

/**
 * Wrapper and styling around AirBnB's [react-dates](https://github.com/airbnb/react-dates).
 *
 * Due to the large size of this module, and its dependency on moment, we have not done a top level re-export.
 */
export function DateRangePicker(props) {
  return (
    <AirBnbDateRangePicker
      navPrev={<Chevron height="20" direction="left" />}
      navNext={<Chevron height="20" direction="right" />}
      {...props}
    />
  );
}

export default DateRangePicker;
