/**
 *
 * Dropdown
 *
 */

import React from 'react';
import PropTypes from 'prop-types';
import './Dropdown.scss';

import { Chevron } from '../../icons/Chevron';

export function Dropdown(props) {
  return (
    <div className="gca-dropdown">
      <button
        className="gca-dropdown-label"
        aria-haspopup={props.open}
        aria-expanded={props.open}
        onClick={props.onLabelClick}
      >
        {props.label}

        <Chevron direction={props.open ? 'up' : 'down'} color="#56A1E3" />
      </button>

      {props.open && <div className="gca-dropdown-menu">{React.Children.toArray(props.children)}</div>}
    </div>
  );
}

Dropdown.propTypes = {
  children: PropTypes.node,
  label: PropTypes.string,
  open: PropTypes.bool,
  onLabelClick: PropTypes.func,
};
