/**
 *
 * EmptyPanel - displayed typically in case of data load error
 * Pass it a title and message to display
 */

import React from 'react';
import PropTypes from 'prop-types';

import './EmptyPanel.scss';

export function EmptyPanel(props) {
  return (
    <div className="gca-empty-panel">
      {props.icon}
      <p>{props.message}</p>
    </div>
  );
}

EmptyPanel.propTypes = {
  icon: PropTypes.node,
  message: PropTypes.node,
};
