/**
 *
 * ErrorPanel - displayed typically in case of data load error
 * Pass it a title and message to display
 */

import React from 'react';
import PropTypes from 'prop-types';

import './ErrorPanel.scss';
import { ErrorRetryIcon } from '../../icons/ErrorRetryIcon';

export function ErrorPanel(props) {
  return (
    <div className="error-panel">
      <ErrorRetryIcon height="65px" />
      <h2>{props.title}</h2>
      <p>{props.message}</p>
    </div>
  );
}

ErrorPanel.propTypes = {
  title: PropTypes.string,
  message: PropTypes.string,
};
