import * as React from 'react';

export interface Props extends React.FieldsetHTMLAttributes<HTMLFieldSetElement> {
  /** Content below the legend section. Has some basic font styles applied but is otherwise freeform. */
  description?: React.ReactNode,
  /** Title for this set of fields. */
  legend: string,
  /** Content for the right column (on-desktop). Freeform. */
  sidebarContent?: React.ReactNode,
  /** Is it disabled? */
  disabled?: boolean,
}

export class Fieldset extends React.Component<Props> {}
export default Fieldset;
