import React from 'react';
import PropTypes from 'prop-types';
import './Layout.scss';

/**
 * The Layout component is the main container for a MicroApp UI.
 */
export function Layout(props) {
  return (
    <div className="gca-layout">
      <div className="gca-title-bar">
        <h1>{props.title}</h1>
        {props.titleBarContent}
      </div>

      <div className="main">{React.Children.toArray(props.children)}</div>
    </div>
  );
}

Layout.propTypes = {
  children: PropTypes.node.isRequired,
  title: PropTypes.string.isRequired,
  titleBarContent: PropTypes.node,
};

export default Layout;
