import { shallow } from 'enzyme';
import React from 'react';

import { LocaleSelect } from './LocaleSelect';

describe('<LocaleSelect />', () => {
  it('basic snapshot', () => {
    const options = [
      { value: 'en-us', label: 'English (US)', code: 'us' },
      { value: 'en-gb', label: 'English (GB)', code: 'gb' },
      { value: 'en-au', label: 'English (AU)', code: 'au' },
      { value: 'de-de', label: 'Deutsche', code: 'de' },
      { value: 'ja-jp', label: '日本語', code: 'jp' },
      { value: 'fr-ca', label: 'Français', code: 'ca' },
      { value: 'es-mx', label: 'Español', code: 'mx' },
      { value: 'nl-nl', label: 'Dutch', code: 'nl' },
    ];

    const wrapper = shallow(<LocaleSelect value="en-us" options={options} />);

    expect(wrapper).toMatchSnapshot();
  });
});
