/**
 *
 * PanelBodySubTitle - left side title and message within panel body
 *
 */

import React from 'react';
import PropTypes from 'prop-types';

import './PanelBodySubTitle.scss';

export function PanelBodySubTitle(props) {
  return (
    <div className="panel-body-subtitle">
      <h3>{props.title}</h3>
      <p>{props.message}</p>
    </div>
  );
}

PanelBodySubTitle.propTypes = {
  title: PropTypes.string,
  message: PropTypes.string,
};
