/*
Source for slider range min/max values:
https://docs.google.com/spreadsheets/d/1dGJlkecc-aiDk0QDI5uz78FSpBMXtdD0H6J47VIgFRk/edit#gid=1703447403
*/

const currencyConfigs = {
  AUD: {
    min: 24,
    max: 161,
    step: 1,
  },
  CAD: {
    min: 19,
    max: 81,
    step: 1,
  },
  JPY: {
    min: 3400,
    max: 16100,
    step: 100,
  },
  MXN: {
    min: 190,
    max: 810,
    step: 10,
  },
  EUR: {
    min: 19,
    max: 81,
    step: 1,
  },
  GBP: {
    min: 14,
    max: 66,
    step: 1,
  },
  USD: {
    min: 19,
    max: 81,
    step: 1,
  },
};

export const getConfig = rawCurrencyCode => {
  if (rawCurrencyCode === undefined) {
    throw new Error("PriceSlider Value attribute must contain a 'currencyCode' property.");
  }
  const currencyCode = rawCurrencyCode.toUpperCase();

  return currencyConfigs[currencyCode] || currencyConfigs.USD;
};
