import { shallow } from 'enzyme';
import React from 'react';

import { RadioButton } from './RadioButton';

describe('<RadioButton />', () => {
  it('unchecked snapshot', () => {
    const wrapper = shallow(<RadioButton />);
    expect(wrapper).toMatchSnapshot();
  });

  it('checked snapshot', () => {
    const wrapper = shallow(<RadioButton checked />);
    expect(wrapper).toMatchSnapshot();
  });

  it('checked disabled snapshot', () => {
    const wrapper = shallow(<RadioButton checked disabled />);
    expect(wrapper).toMatchSnapshot();
  });

  it('unchecked disabled snapshot', () => {
    const wrapper = shallow(<RadioButton disabled />);
    expect(wrapper).toMatchSnapshot();
  });

  it('string label snapshot', () => {
    const wrapper = shallow(<RadioButton label="ima label" />);
    expect(wrapper).toMatchSnapshot();
  });

  it('node label snapshot', () => {
    const wrapper = shallow(
      <RadioButton
        label={
          <span>
            A <b>label</b>
          </span>
        }
      />,
    );
    expect(wrapper).toMatchSnapshot();
  });
});
