import { shallow } from 'enzyme';
import React from 'react';
import { Table } from './Table';

describe('<Table />', () => {
  it('should render ', () => {
    const wrapper = shallow(
      <Table>
        <caption>A Caption</caption>
        <thead>
          <tr>
            <th className="col-date">Date</th>
            <th className="col-description sortasc">Description</th>
          </tr>
        </thead>

        <tbody>
          <tr>
            <td>01/31/2016</td>
            <td colSpan="2">Invoice #458998</td>
          </tr>

          <tr>
            <td>01/29/2016</td>
            <td colSpan="2">Invoice #12345</td>
          </tr>
        </tbody>

        <tfoot>
          <tr>
            <td colSpan="4">Total Balance</td>
            <td className="text-align-right">$683.21</td>
          </tr>
        </tfoot>
      </Table>,
    );

    expect(wrapper).toMatchSnapshot();
  });

  it('should render with custom class', () => {
    const wrapper = shallow(
      <Table className="my-custom-class">
        <tbody>
          <tr>
            <td>01/31/2016</td>
            <td colSpan="2">Invoice #458998</td>
          </tr>

          <tr>
            <td>01/29/2016</td>
            <td colSpan="2">Invoice #12345</td>
          </tr>
        </tbody>
      </Table>,
    );

    expect(wrapper).toMatchSnapshot();
  });
});
