import { shallow } from 'enzyme';
import * as React from 'react';

import TimeRange from './TimeRange';

const testProps = {
  firstSelect: {
    start: '00:00',
    end: '23:30',
    increment: 30,
  },
  secondSelect: {
    start: '00:00',
    end: '23:30',
    increment: 30,
  },
  outputWithSeconds: true,
  locale: 'en-us',
  labels: {
    start: 'Start',
    end: 'End',
    to: 'to',
    invalid: 'Please select a start time that is before your end time.',
  },
  valid: true,
};

const minimumProps = {
  firstSelect: {
    increment: 30,
  },
  secondSelect: {
    increment: 30,
  },
  locale: 'en-us',
};

const disabledProps = {
  firstSelect: {
    start: '00:00',
    end: '23:30',
    increment: 30,
  },
  secondSelect: {
    start: '00:00',
    end: '23:30',
    increment: 30,
  },
  outputWithSeconds: true,
  locale: 'en-us',
  labels: {
    start: 'Start',
    end: 'End',
    to: 'to',
    invalid: 'Please select a start time that is before your end time.',
  },
  valid: true,
  disabled: true,
};

describe('<TimeRange />', () => {
  describe('TimeRange', () => {
    it('should match snapshot', () => {
      expect(
        shallow(<TimeRange {...minimumProps} /> ) ).toMatchSnapshot();
    });

    it('should match snapshot when invalid', () => {
      expect(
        shallow(<TimeRange {...testProps} /> ) ).toMatchSnapshot();
    });

    it('should match snapshot when disabled', () => {
      expect(
        shallow(<TimeRange {...disabledProps } />)).toMatchSnapshot();
    });
  });
});
