import { shallow } from 'enzyme';
import React from 'react';

import { TimeRangeField } from './TimeRangeField';

const baseProps = {
  startName: 'startTime',
  endName: 'endTime',
  startRange: '00:00',
  endRange: '23:30',
  increment: 30,
  valid: true,
  labels: {
    start: 'Start',
    end: 'End',
    to: 'to',
    invalid: 'Please select a start time that is before your end time.',
  },
};

describe('<TimeRangeField />', () => {
  describe('TimeRangeField', () => {
    it('should match snapshot', () => {
      expect(
        shallow(<TimeRangeField {...baseProps} />, {
          context: { _reduxForm: { syncErrors: {} } },
        }),
      ).toMatchSnapshot();
    });

    it('should match snapshot when invalid', () => {
      expect(
        shallow(<TimeRangeField {...baseProps} />, {
          context: { _reduxForm: { syncErrors: { startTime: 'invalid selection' } } },
        }),
      ).toMatchSnapshot();
    });
  });
});
