import * as React from 'react';
import { customFinalField } from './customfield';

import Checkbox, { Props as CheckboxProps  } from '../components/Checkbox/Checkbox';

/**
 * CheckboxField react-final-form field for gca-react-components Checkbox
 */
export const CheckboxFinalField = customFinalField<CheckboxProps>(
  (  { disabled, indeterminate, label, valid }, { input } ) => (
    <Checkbox
      checked={!!input.value}
      disabled={disabled}
      onClick={input.onChange}
      indeterminate={indeterminate}
      label={label}
      valid={valid}
      {...input}
    />
  )
);

/** @component */
export default CheckboxFinalField;