import * as React from 'react';
import { customFinalField } from './customfield';
import * as moment from 'moment';

import DateInput, { Props as DateInputProps } from '../components/DateInput/DateInput';

/**
 * DateInputField react-final-form field for gca-react-components DateInput
 */
export const DateInputFinalField = customFinalField<DateInputProps>(
  (  { valid, invalidMessage }, { input } ) => (
    <DateInput
      invalidMessage={invalidMessage}
      valid={valid}
      {...input}
    />
  )
);

/** @component */
export default DateInputFinalField;