import { shallow } from 'enzyme';
import * as React from 'react';

import { DateRangePickerFinalField } from './DateRangePickerFinalField';

describe('<DateRangePickerFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <DateRangePickerFinalField
          name="myinput"
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <DateRangePickerFinalField
          name="myinput"
          valid={false}
        />)
      ).toMatchSnapshot();
  });

});
