import { shallow } from 'enzyme';
import * as React from 'react';

import { InputFinalField } from './InputFinalField';

describe('<InputFinalField />', () => {
  
  it('should render the basic field', () => {
    expect(
      shallow(
        <InputFinalField
          name="mytext"
          placeholder="sample placeholder"
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <InputFinalField
          name="mytext"
          placeholder="sample placeholder"
          valid={false}
        />)
      ).toMatchSnapshot();
  });

});
