import { shallow } from 'enzyme';
import * as React from 'react';

import { LocaleSelectFinalField } from './LocaleSelectFinalField';

describe('<LocaleSelectFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <LocaleSelectFinalField
          name="mylocale"
          placeholder="sample placeholder"
          options={[
            { value: 'en-us', label: 'English (US)', code: 'us' },
            { value: 'en-gb', label: 'English (GB)', code: 'gb' }
          ]}
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <LocaleSelectFinalField
          name="mylocale"
          placeholder="sample placeholder"
          options={[
            { value: 'en-us', label: 'English (US)', code: 'us' },
            { value: 'en-gb', label: 'English (GB)', code: 'gb' }
          ]}
          valid={false}
        />)
      ).toMatchSnapshot();
  });

});
