import * as React from 'react';
import * as moment from 'moment';

import { customFinalField } from './customfield';

import PhoneInput, { Props as PhoneInputProps } from '../components/PhoneInput/PhoneInput';

/**
 * CheckboxField react-final-form field for gca-react-components PhoneInput
 */
export const PhoneInputFinalField = customFinalField<PhoneInputProps>(
  (  { countryCode, disabled, locale, valid, phoneFormat }, { input } ) => (
    <PhoneInput
      countryCode={countryCode}
      disabled={disabled}
      locale={locale}
      phoneFormat={phoneFormat}
      {...input}
    />
  )
);

/** @component */
export default PhoneInputFinalField;
