import { shallow } from 'enzyme';
import * as React from 'react';

import { PriceSliderFinalField } from './PriceSliderFinalField';

describe('<PriceSliderFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <PriceSliderFinalField
          name="myprice"
          locale="en-us"
          defaultValue={{ minValue: 30, maxValue: 60, currencyCode: 'USD' }}
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <PriceSliderFinalField
          name="myprice"
          locale="en-us"
          defaultValue={{ minValue: 30, maxValue: 60, currencyCode: 'USD' }}
          valid={false}
        />)
      ).toMatchSnapshot();
  });

});
