import { shallow } from 'enzyme';
import * as React from 'react';

import { ProfileHoursFinalField } from './ProfileHoursFinalField';

describe('<ProfileHoursFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <ProfileHoursFinalField
          name="myshifts"
          standardShifts={[
            { id: 'brunch', label: 'Brunch' },
            { id: 'lunch', label: 'Lunch' }
          ]}
          errors={[
            { timeRange: 'time error',
              days: 'day error',
              shiftName: 'shift error'
            }]}
          labels={{
            timeRangeTo: 'to',
            addShift: 'Add a shift',
            removeShift: 'remove',
            shiftEnds: 'End',
            shiftStarts: 'Start',
            lastSeating: 'Last Seating',
            invalidName: 'Please select a name',
            invalidDays: 'Please select at least one day',
            invalidTimeRange: 'Please select a start time before the end time',
          }}
          locale="en-us"
          countryCode="us"
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <ProfileHoursFinalField
          name="myshifts"
          standardShifts={[
            { id: 'brunch', label: 'Brunch' },
            { id: 'lunch', label: 'Lunch' }
          ]}
          errors={[
            { timeRange: 'time error',
              days: 'day error',
              shiftName: 'shift error'
            }]}
          labels={{
            timeRangeTo: 'to',
            addShift: 'Add a shift',
            removeShift: 'remove',
            shiftEnds: 'End',
            shiftStarts: 'Start',
            lastSeating: 'Last Seating',
            invalidName: 'Please select a name',
            invalidDays: 'Please select at least one day',
            invalidTimeRange: 'Please select a start time before the end time',
          }}
          locale="en-us"
          countryCode="us"
          valid={false}
        />)
      ).toMatchSnapshot();
  });

});
