import { shallow } from 'enzyme';
import * as React from 'react';

import { SelectFinalField } from './SelectFinalField';

describe('<SelectFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <SelectFinalField
          name="myselect"
          options={[
            { value: 'one', label: 'First' },
            { value: 'two', label: 'Second' },
            { value: 'three', label: 'Third' }
          ]}
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <SelectFinalField
          name="myselect"
          options={[
            { value: 'one', label: 'First' },
            { value: 'two', label: 'Second' },
            { value: 'three', label: 'Third' }
          ]}
          valid={false}
        />)
      ).toMatchSnapshot();
  });

});
