import * as React from 'react';
import * as moment from 'moment';

import { customFinalField } from './customfield';

import TimeSelect, { Props as TimeSelectProps } from '../components/TimeSelect/TimeSelect';

/**
 * TimeSelectField react-final-form field for gca-react-components TimeSelect
 */
export const TimeSelectFinalField = customFinalField<TimeSelectProps>(
  ({ disabled, locale, start, end, increment, valid, outputWithSeconds }, { input }) => {
    return (
      <TimeSelect
        value={input.value}
        disabled={disabled}
        onChange={input.onChange}
        locale={locale}
        outputWithSeconds={outputWithSeconds}
        start={start}
        end={end}
        increment={increment}
      />
    );
  },
);

/** @component */
export default TimeSelectFinalField;
