import React from 'react';
import PropTypes from 'prop-types';
import dimensionWrapper from './dimensionWrapper';

const rotationMap = {
  up: 180,
  down: 0,
  right: 270,
  left: 90,
};

export const Chevron = dimensionWrapper((props) => {
  const rotation = rotationMap[props.direction];

  return (
    <svg height={props.height} width={props.width} viewBox="0 0 10 10" version="1.1" xmlns="http://www.w3.org/2000/svg">
      <title>{props.direction} chevron</title>
      <polyline
        fill="none"
        stroke={props.color}
        points="1 3 5 7 9 3"
        transform={`rotate(${rotation} 5 5)`}
      />
    </svg>
  );
});

Chevron.propTypes = {
  color: PropTypes.string,
  direction: PropTypes.oneOf(['up', 'down', 'left', 'right']),
  height: PropTypes.string,
  width: PropTypes.string,
};

Chevron.defaultProps = {
  color: '#878787',
  direction: 'down',
  height: '13',
};

export { Chevron as ToggleChevron };
