import { isGoodRatio, getDimensions } from './FlagIcon';

describe('<FlagIcon />', () => {
  describe('isGoodRatio', () => {
    it('should validate good ratios', () => {
      expect(isGoodRatio({ width: '4', height: '3' })).toBe(true);
      expect(isGoodRatio({ width: '40', height: '30' })).toBe(true);
    });

    it('should validate ratios that are close', () => {
      expect(isGoodRatio({ width: '40', height: '29' })).toBe(true);
      expect(isGoodRatio({ width: '40', height: '31' })).toBe(true);
    });

    it('should not validate bad ratios', () => {
      expect(isGoodRatio({ width: '30', height: '40' })).toBe(false);
      expect(isGoodRatio({ width: '40', height: '32' })).toBe(false);
    });
  });

  describe('getDimensions', () => {
    const defaultProps = { width: '40', height: '30' };

    it('should return default dimensions for no props', () => {
      expect(getDimensions()).toEqual(defaultProps);
    });

    it('should calculate height from width', () => {
      expect(getDimensions({ width: '40' })).toEqual(defaultProps);
    });

    it('should calculate width from height', () => {
      expect(getDimensions({ height: '30' })).toEqual(defaultProps);
    });

    it('should return complete passed in dimensions', () => {
      expect(getDimensions(defaultProps)).toEqual(defaultProps);
    });
  });
});
