import { ActionsObservable } from 'redux-observable';

export function fetchFeatureTogglesEpic(action$: ActionsObservable<any>, store: any, deps: any): ActionsObservable<any>;
export function featureTogglesEpic(action$: ActionsObservable<any>, store: any, deps?: any): ActionsObservable<any>;

export interface FeatureToggles {
  isFetching: boolean;
  fetchFailed: boolean;
  features: {
    [featureName: string]: boolean
  };
  error: null | Object;
}

export const initialState: FeatureToggles;

export function featureTogglesReducer(state: FeatureToggles, action: any): FeatureToggles

interface Selectors {
  featureToggles: (state: any) => FeatureToggles;
  featureToggle: (state: any, featureToggleKey: string) => boolean;
}

export const selectors: Selectors;
