import getTimeFormat from './timeFormats';

describe('getTimeFormats', () => {
  it('should always return either "12" or "24"', () => {
    expect(getTimeFormat()).toBe('h:mm a');
    expect(getTimeFormat('en-us')).toBe('h:mm a');
    expect(getTimeFormat('us')).toBe('h:mm a');
    expect(getTimeFormat('jp')).toBe('HH:mm');
    expect(getTimeFormat('ja-jp')).toBe('HH:mm');
    expect(getTimeFormat('not a country')).toBe('h:mm a');
  });
});
