/** Convert sequences of one or more whitespace characters to a single space */
export const stripWhiteSpace = s => s.replace(/\s+/g, ' ');

/** Replace all variations of break tags to spaces */
export const stripBreakTags = s => s.replace(/<[bB][rR]\s?\/?>/g, ' ');

/** Convert sequences of one or more whitespace characters or break tags to a single space */
export const cleanString = s => stripWhiteSpace(stripBreakTags(s));
