import { cleanString, stripBreakTags, stripWhiteSpace } from './string';

describe('String Utils', () => {
  it('cleanString', () => {
    expect(cleanString('<br /> <br/> <br> <BR /> <BR/> <BR>')).toEqual(' ');
  });

  it('stripBreakTags', () => {
    expect(stripBreakTags('<br /><br/><br><BR /><BR/><BR>')).toEqual('      ');
  });

  it('stripWhiteSpace', () => {
    expect(stripWhiteSpace('\n\r ')).toEqual(' ');
  });
});
