const baseConfig = require('./styleguide.config');
const version = require('./package').version.replace(/\./g, '-');
const cdnPath = `https://gc-cdn.otstatic.com/gc-microapps/prod/react-components-styleguide/${version}/`;

module.exports = {
  ...baseConfig,
  dangerouslyUpdateWebpackConfig(webpackConfig, env) {
    return {
      ...webpackConfig,
      output: {
        ...webpackConfig.output,
        publicPath: cdnPath,
      },
    };
  },
};
