import { IntlProvider as BuffetIntlProvider, registerMessages } from '@buffet/intl';
import React from 'react';
import ReactDOM from 'react-dom';

import { translationMessages } from './translations/i18n';

import ExampleComponent from './ExampleComponent/ExampleComponent';

import './app.scss';
registerMessages(translationMessages);

const render = () => {
  ReactDOM.render((
    <BuffetIntlProvider>
      <main className="basic-class">
        <h1>Welcome to the monorepo Registry MicroApp example!</h1>
        <img src="/assets/Under_Construction.gif"/>
        <ExampleComponent/>
      </main>
    </BuffetIntlProvider>
  ),
    document.getElementById('app'),
  );
};

render();
