import { shallow } from 'enzyme';
import React from 'react';

import { SortControl } from './SortControl';


describe('ItemCell', () => {

  it('should render the basic as the snapshot', () => {
    const wrapper = shallow(
      <SortControl
        onUp={jest.fn}
        onDown={jest.fn}
      />,
    );
    expect(wrapper).toMatchSnapshot();
  });

  it('should render the basic as the snapshot', () => {
    const wrapper = shallow(
      <SortControl
        onUp={jest.fn}
        onDown={jest.fn}
        active="down"
      />,
    );
    expect(wrapper).toMatchSnapshot();
  });

  it('should render the basic as the snapshot', () => {
    const wrapper = shallow(
      <SortControl
        onUp={jest.fn}
        onDown={jest.fn}
        active="up"
      />,
    );
    expect(wrapper).toMatchSnapshot();
  });


});
