import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';

import { PureRoleDefinitions as RoleDefinitions } from './RoleDefinitions';

describe('<RoleDefinitions />', () => {

  it('should render the group as the snapshot', () => {
    const wrapper = shallowWithIntl(<RoleDefinitions type="group" />);
    expect(wrapper).toMatchSnapshot();
  });

  it('should render the restaurant as the snapshot', () => {
    const wrapper = shallowWithIntl(<RoleDefinitions type="restaurant" />);
    expect(wrapper).toMatchSnapshot();
  });

});
