import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';

import { RolePermissionListImpl as RolePermissionList } from './RolePermissionList';

describe('<RolePermissionList />', () => {

  const role = {
    permissions: [ 'perm1', 'perm2' ],
    properties: {},
    urn: 'fake:role',
  };

  const permissions = ['perm1', 'perm2', 'perm3'];

  it('should render the basic as the snapshot', () => {
    const wrapper = shallowWithIntl(
      <RolePermissionList
        info={role}
        permissions={permissions}
        type="group"
        editable={true}
        onEdit={jest.fn()}
      />);
    expect(wrapper).toMatchSnapshot();
  });

});
