import { SortOrder } from '../store/types';
import { propsToQuerystring, sortedToProps } from './querystring-helpers';

describe('querystring helpers', () => {

  it('propsToQuerystring() converts an object to a queryString', () => {
    const testObj = {
      a: 'test', b: 'second test',
    };
    const qs = propsToQuerystring(testObj);
    expect(qs).toEqual('?a=test&b=second%20test');
  });

  it('sortedToProps() convert a sorted obj to a SortParameters object', () => {
    const sorted = {
      field: 'asc',
    } as {[id: string]: SortOrder};
    const sortParameters = sortedToProps(sorted);
    expect(sortParameters.sort).toEqual('field');
    expect(sortParameters.order).toEqual('asc');
  })
});
