import { shallow } from 'enzyme';
import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';

import { PureConfirmHeader as ConfirmHeader } from './ConfirmHeader';

describe('<ConfirmHeader />', () => {
  it.skip('should display message for a scheduled appointment, with specific appointment info', () => {
    const props = {
      appointmentDate: '2018-10-18T14:00:00-07:00',
      confirmPageDisplayState: 'Scheduled_RedirectedFromScheduling',
    };
    const wrapper = shallowWithIntl(<ConfirmHeader {...props}/>);
    expect(wrapper).toMatchSnapshot();
  });

  it('should display message for a scheduled appointment, with generic info', () => {
    const props = {
      confirmPageDisplayState: 'Scheduled_LoadedFromUrl',
    };
    const wrapper = shallowWithIntl(<ConfirmHeader {...props}/>);
    expect(wrapper).toMatchSnapshot();
  });

  it('should display message for an unscheduled appointment', () => {
    const props = {
      confirmPageDisplayState: 'Unscheduled_SchedulingSkipped',
      contractOwner: {
        firstName: 'Ned',
        lastName: 'Stark',
      },
    };
    const wrapper = shallowWithIntl(<ConfirmHeader {...props} />);
    expect(wrapper).toMatchSnapshot();
  });
});
