import { shallow } from 'enzyme';
import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';

import { ACCEPT_CTA_STATE, PureTermsCallToAction as TermsCallToAction, SCROLL_CTA_STATE } from './TermsCallToAction';

describe('<TermsCallToAction />', () => {

  it('should render in scroll state', () => {
    const wrapper = shallowWithIntl(<TermsCallToAction ctaState={SCROLL_CTA_STATE} />);
    expect(wrapper).toMatchSnapshot();
  });

  it('should render in accept state', () => {
    const wrapper = shallowWithIntl(<TermsCallToAction ctaState={ACCEPT_CTA_STATE} />);
    expect(wrapper).toMatchSnapshot();
  });
});
